var pageNav=pageNav||{};pageNav.fn=null;pageNav.url=null;pageNav.nav=function(p,pn,url){pageNav.url=url;if(pn<=1){this.p=1;this.pn=1;return""};if(pn<p){p=pn};var re="";if(p<=1){p=1}else{re+=this.pHtml(p-1,pn,"上一页");re+=this.pHtml(1,pn,"1")};this.p=p;this.pn=pn;var start=2;var end=(pn<9)?pn:9;if(p>=7){re+="...";start=p-4;var e=p+4;end=(pn<e)?pn:e};for(var i=start;i<p;i++){re+=this.pHtml(i,pn)};re+=this.pHtml2(p);for(var i=p+1;i<=end;i++){re+=this.pHtml(i,pn)};if(end<pn){re+="...";re+=this.pHtml(pn,pn)};if(p<pn){re+=this.pHtml(p+1,pn,"下一页")};return re};pageNav.pHtml=function(pageNo,pn,showPageNo){showPageNo=showPageNo||pageNo;var H=" <a href='"+pageNav.url.replace("{pn}",pageNo)+"' style='border: 1px solid #999;padding:2px 8px;display: inline-block;'>"+showPageNo+"</a> ";return H};pageNav.pHtml2=function(pageNo){var H=" <span style='font-weight: bold;padding:2px 5px;float:none;'>"+pageNo+"</span> ";return H};pageNav.go=function(p,pn,url){document.getElementById("pageNav").innerHTML=this.nav(p,pn,url);if(this.fn!=null){this.fn(this.p,this.pn)}};